    def logging_enable_all():
        config = []
        ssid = 0
        while(ssid < 0x2888):
            cmd = b'\x7d\x04'
            cmd += struct.pack('<H', ssid)
            cmd += struct.pack('<H', ssid)
            cmd += struct.pack('<H', 0)
            cmd += b'\xff\xff\xff\xff'
            ssid += 1
            config.append(cmd)
        return config
    def logging_disable_all():
        config = []
        ssid = 0
        while(ssid < 0x2888):
            cmd = b'\x7d\x04'
            cmd += struct.pack('<H', ssid)
            cmd += struct.pack('<H', ssid)
            cmd += struct.pack('<H', 0)
            cmd += b'\x00\x00\x00\x00'
            ssid += 1
            config.append(cmd)
        return config
