    def read_loop(ser, utility):

        TRAILER_CHAR = b'\x7e'
        read_buffer = b''

        num_reconnect_retries = 0

        while True:
                
            # Read more bytes until a trailer character is found

            read_size = 64
            data_read = b""
            while TRAILER_CHAR not in read_buffer:
                
                # try:
                data_read = bytes(ser.read(read_size))
                print(data_read)
                    # assert data_read
                
                # except Exception:

                #     traceback.print_exc()

                #     # Retry loop.

                #     if num_reconnect_retries >= 3:
                #         error('Connection to the USB link lost despite retries')
                #         ser.close()
                #         exit()
                #     sleep(2)
                #     num_reconnect_retries += 1

                # else:
                    # num_reconnect_retries = 0
                
                read_buffer += data_read
            
            # Decapsulate and dispatch
            
            while TRAILER_CHAR in read_buffer:

                end_pos = read_buffer.index(TRAILER_CHAR) + 1
                raw_payload = read_buffer[:end_pos]
                read_buffer = read_buffer[end_pos:]

                if raw_payload == TRAILER_CHAR:
                    warning('(Received an empty diag frame)')
                elif len(raw_payload) < 3:
                    warning('(Received a too short diag frame)')
                
                else:
                    
                    try:
                    
                        unframed_message = utility.hdlc_decapsulate(
                            payload = raw_payload
                        )
                    
                    except Exception:
                        
                        # The first packet that we receive over the Diag input may
                        # be partial
                        
                        continue
                    
                    finally:
                        
                        received_first_packet = True
                    try:
                        if unframed_message[0] == 0x79:
                            print(unframed_message[20:], file=out, flush = True)
                    except:
                        continue


